result.format:
{% if execution.status == 'succeeded' %}
{% for line in execution.result.result %}
   {% for ec2 in line -%}
   {{ ec2.id }}  - {{ ec2.state }} -- {{ ec2.tags.Name }} -- {{ ec2.ip_address }}
   {% endfor %}
{% endfor %}
{% else %}
Error: {{execution.result.stdout}}{~}See {{execution.id}} for more details.
{% endif %}
